let tag=document.createElement("script");tag.src="https://www.youtube.com/iframe_api",document.body.appendChild(tag);let currentPlayer=null;document.querySelectorAll(".video-preview").forEach(e=>{e.querySelector(".video-preview__play").addEventListener("click",()=>{currentPlayer&&(currentPlayer.stopVideo(),currentPlayer.container.innerHTML=currentPlayer.previewContent,currentPlayer=null);const t=e.dataset.video,n=e.innerHTML;e.innerHTML=`<div id="player-${t}"></div>\n                         <button class="video-preview__stop">âœ–</button>`;const r=e.querySelector(".video-preview__stop"),l=new YT.Player(`player-${t}`,{videoId:t,width:"100%",height:"100%",playerVars:{autoplay:1,controls:1,rel:0}});currentPlayer={player:l,container:e,previewContent:n},r.addEventListener("click",()=>{l.stopVideo(),e.innerHTML=n,currentPlayer=null})})});const burger=document.getElementById("burger"),menu=document.getElementById("mobileMenu"),closeBtn=document.getElementById("closeMenu"),overlay=document.getElementById("overlay");function openMenu(){menu.classList.add("active"),overlay.classList.add("active")}function closeMenu(){menu.classList.remove("active"),overlay.classList.remove("active")}burger.addEventListener("click",openMenu),closeBtn.addEventListener("click",closeMenu),overlay.addEventListener("click",closeMenu);